import React, { Component } from 'react'
// import './BasePage.css'
// import ItemBlock from '../Studycomponents/ItemBlock';
// import ItemComponent from '../Studycomponents/ItemComponent';

export default class BasePage extends Component {
    state = {
        study_data : [],
        items: [],
        show: 'all',
        emailobj: '',
        bgcolorall: 'rgb(54, 133, 235)',
        bgcolorfile: '',
        bgcolorvideo: ''

    };
    async componentDidMount() {
        try {
            // fetching the list of created class
            // const rooms = await fetch('http://127.0.0.1:8000/data/studymaterial_class_list')
            // const class_data = await rooms.json();
            // console.log('this is class data', class_data)
            // const class_id = this.props['classid']
            // const filtered_class = class_data.filter(function(item){return item.id === class_id})
            // console.log('this is email', filtered_class[0].email)
            // fetching the list of study material of respective id
            const res = await fetch('http://127.0.0.1:8000/assignment/assignmentpdf_list'+this.props['classid']); // fetching the data from api, before the page loaded
            const study_data = await res.json();
            const items = study_data
            this.setState({
                // study_data,
                items,
                // emailobj: filtered_class[0] 
            });
        } catch (e) {
            console.log(e);
        }
        console.log('this is id of current user', this.props['classid'])
        console.log('this is assignment list', this.state.items)
      }
     
    render() {
        console.log('SMT', this.state.items)
        return (
            <div className = "assignment-div">
                <h2 className = "assignment-header">Assignemnt</h2>
                <div className = "assignment-nav">
                    {/* <ItemBlock data = {this.state.emailobj} ></ItemBlock> */}
                </div>
                <div className = 'assignment-content-div'>
                    
                    {
                        this.state.items.reverse().map(studyobject =>{
                            // display content having only videos
                                // return <ItemComponent linkObject = {studyobject.videos} imgsrc = {'https://img.icons8.com/fluent/48/000000/video.png'} title = {studyobject.video_title} created_time = {studyobject.created_at.split('T')[0]} key = {studyobject.id}></ItemComponent>
                            return <h1>Hello guys</h1>
                        })
                    }
                </div>
            </div>
        );
    }
}
