import React, { Component } from 'react'

export default class Userdata extends Component {
    state = {
        obj:[],
        userdetail:[],
        myclass:[],
        username: '',
        email: '',
        first_name: ''
    }
    componentDidMount=()=>{

        fetch('http://127.0.0.1:8000/user/token/',{
            method: 'GET'
        }).then(response => response.json())
        .then(result => {
            console.log('this is result : ', result)
            this.setState({
                userdetail: result.filter(function(items){return items.key === localStorage.getItem('token')})
            })
        })

        // fetching class room
        fetch('http://127.0.0.1:8000/data/studymaterial_class_list/',{
            method: 'GET',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': 'Token '+localStorage.getItem('token')
            },
            
        
        }).then((respone)=>respone.json())
        .then(result =>{
            this.setState({
                obj: result.filter(function(value){return value.email === this.state.userdetail.email})
            })
        })

        // fetching user detail
        console.log('Token ', localStorage.getItem('token'))
        
    }
    onLogout =()=>{
        localStorage.removeItem('token')
        window.location.reload();
    }
    onCreateClass =()=>{

    }
    render() {
        console.log(this.state.obj)
        console.log('this is test: ',this.state.userdetail)
        // this.setState({
        //     myclass:this.state.obj.filter(function(value){return value.email === 'admin@admin.com'})
        // })
        return (
            <div>
                <button onClick = {this.onLogout}>Logout</button>
                <button onClick = {this.onCreateClass}>Create Class</button>
        {/* <h1>{this.state.obj[0].username}</h1> */}
                {
                    this.state.obj.map(function(item){
                        return (<div>
                         <h3>{item.classname}</h3>
                        <h3>{item.email}</h3>
                        </div>)
                    })
                }
            </div>
        )
    }
}
