import React, { Component } from 'react'
import './UserProfile.css'
import cancel_icon from '../images/cancel_icons.png'

export default class UserProfile extends Component {
    
    state = {
        showProfileCart: false,
        editbio: false,
        editaddress: false,
        content: 'this is bio data',
        address: '',
        bio: '',
        profileImg: ''
    }
    onChangeInfo =(event) =>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    onChangeProfileImg = (event) =>{
        this.setState({
            [event.target.name]: event.target.file
        })
    }
    render() {
        return (
            <div className = "card">
                <div className = "cancel-div">
                    <img src = {cancel_icon} alt = "images"></img>
                </div>
                <img className = 'capture-img' src="https://img.icons8.com/android/24/000000/camera.png"/><input className = "get-img" type = 'file'/>
                <img className = 'profile-img' src={this.props['userprofile'].profileImg} alt="John" style={{width:100}}></img>
                <h1>Username</h1>
                <p className="title">Full name</p>
                <p>email</p>
                <input type = "text" disabled value = "this is bio"></input>
                <img className = 'edit-icon' src="https://img.icons8.com/windows/16/000000/edit.png"/>
                <input type = "text" disabled value = "this is address"></input>
                <img className = 'edit-icon' src="https://img.icons8.com/windows/16/000000/edit.png"/>
                
                <button className = "edit-submit">Edit Details</button>
            </div>
        )
    }
}
