import React, { Component } from 'react'

export default class SMaterialBlock extends Component {
    state = [
        study_data = []
    ]

    async componentDidMount() {
        try {
          const res = await fetch('http://127.0.0.1:8000/data/studymaterial_material_list/2'); // fetching the data from api, before the page loaded
          const study_data = await res.json();
          console.log(study_data)
          this.setState({
            study_data
          });
        } catch (e) {
          console.log(e);
        }
      }
    render() {
        return (
            <div>
                <h1>This is study material</h1>
                {
                    this.state.study_data.map(item =>(
                        <div key = {item.id}>
                            <h2>{item.files}</h2>
                            <video width="400" controls>
                                <source src = {item.videos}></source>
                            </video>
                        <h2>{item.created_at}</h2>
                        <h2>{item.updated_at}</h2>
                        </div>
                    ))
                }
            </div>
        )
    }
}
