import React, { Component } from 'react'
import './UserLogin.css'

export default class UserLogin extends Component {
    state = {
        username: '',
        password: ''
    }
    jumpSignup = () =>{
        this.props.showsignup()
    }
    handleLoginSubmit =(event)=>{
        const formdata = FormData()
        formdata.append()
    }
    onChangeinput =(event)=>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    render() {
        const {username, password} = this.state
        return (
            <div>
                <form className = "signin-form">
                    <h2>User Login</h2>
                    <label>Username</label>
                    <input type = "text" name = "username" value = {username} required></input>
                    <label>Password</label>
                    <input type = "password" value = {password} required></input>
                    <a href = "https://www.google.com/"><p className = "forget-pass">Forgot password? reset password</p></a>
                    <button type = "submit">Login</button>
                    <p className = "create-acc" onClick = {this.jumpSignup}>Don't have account? Create one</p>
                </form>
            </div>
        )
    }
}
