import React, { Component } from 'react'
import cancel_icon from '../images/cancel_icons.png'
import './AssignmentCheck.css'

export default class RateAssignment extends Component {
    state = {
        gradevalue:''
    }
    handleSubmit =()=>{
        console.log('submitted')
        // const RateAnswer = new FormData()
        // RateAnswer.append('submitted_asignment', this.props['assignemntId'])
        // RateAssignment.append('score', this.state.gradevalue)
        console.log('grade', this.state.gradevalue)
        // fetch('http://127.0.0.1:8000/assignment/assignmentsubmit/create/',{
        //     method: 'POST',
        //     body: RateAssignment
        // }).then(response=> response.json())
        // .then(result=>{
        //     console.log('success grade:', result)
        // }).then(error=>{
        //     console.log('error grade:', error)
        // })
    }
    onhideForm =()=>{
        this.props.hideGradeForm()
    }
    onChangeInput=(event)=>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    render() {
        const {gradevalue} = this.state
        return (
            <form className = "rate-answer-div" onSubmit = {this.handleSubmit}>
                <img src = {cancel_icon} alt = "" onClick = {this.onhideForm}></img>
                <input type = 'text' name = "gradevalue" value = {gradevalue} onChange = {this.onChangeInput}></input>
                <button type='submit' className = "" required>Submit</button>
            </form>
        )
    }
}
