import React, { Component } from 'react'
// import SMaterialBlock from './Studycomponents/SMaterialBlock'
// import UserLogin from './Authentication/UserLogin'
import UserSignUp from './Authentication/UserSignup'
import UserLogin from './Authentication/UserLogin'
// import UserLogin from './Authentication/UserLogin'
import {BrowserRouter, Route} from 'react-router-dom'

export default class App extends Component {
  state = {
    registered: false,
  }
  showLoginForm = () =>{
    this.setState({
      registered: true,
    })
  }
  render() {
    return (
      <div>
        {/* <UserSignUp></UserSignUp> */}
        <BrowserRouter>
        {
          (this.state.registered === true)?
          <Route exact path = "/login" render = {()=>(<UserLogin></UserLogin>)}></Route>
          : <Route path = "/signup" render = {()=>(<UserSignUp data = {this.showLoginForm}></UserSignUp>)}></Route>
        }
        </BrowserRouter>
        {/* <UserLogin></UserLogin> */}
        {/* <SMaterialBlock></SMaterialBlock> */}
      </div>
    )
  }
}
