import React, { Component } from 'react'
import './UserProfile.css'
import cancel_icon from '../images/cancel_icons.png'

export default class UserProfile extends Component {
    
    state = {
        showProfileCart: false,
        editbio: true,
        editaddress: true,
        showsubmitbtn: false,
        content: 'this is bio data',
        userdetail: [],
        address: '',
        bio: '',
        profileImg: ''
    }
    componentDidMount =()=>{
        this.setState({
            userdetail: this.props['myinfo']
        })
    }
    onhandleSubmit =(event)=>{
        // updating the profile 
        const newdata = new FormData()
        newdata.append('profileImg', this.state.profileImg[0])
        newdata.append('address', this.state.address)
        newdata.append('bio', this.state.bio)
        newdata.append('userid', this.props['userprofile'].userid)
        fetch('http://127.0.0.1:8000/data/userinfo_update/'+this.props['userprofile'].userid,{
            method: 'POST',
            body: newdata
        }).then(response => response.json())
        .then(result => {
            console.log('this is updated result', result)
        })
    }
    onChangeInfo =(event) =>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }
    onChangeProfileImg = (event) =>{
        this.setState({
            [event.target.name]: event.target.files
        })
    }
    onEditBio = () =>{
        if(this.state.editbio === true){
            this.setState({
                editbio: false,
                showsubmitbtn: true
            })
        }
        else{
            this.setState({
                editbio: true
            })
        }
        
    }
    onEditAddress=()=>{
        if(this.state.editaddress === true){
            this.setState({
                editaddress: false,
                showsubmitbtn: true
            })
        }
        else{
            this.setState({
                editaddress: true
            })
        }
    }
    render() {
        const {bio, address} = this.state
        console.log('this is userdetail', this.state.userdetail)
        return (
            <div className = "card">
                <div className = "cancel-div">
                    <img src = {cancel_icon} alt = "images"></img>
                </div>
                <img className = 'capture-img' src="https://img.icons8.com/android/24/000000/camera.png" alt = "" /><input className = "get-img" type = 'file' name = 'profileimg' onChange = {this.onChangeProfileImg}/>
                {
                    (this.state.profileImg === '')?
                    <img className = 'profile-img' src={this.props['userprofile'].profileImg} alt="John" style={{width:100}}></img>
                    :<img className = 'profile-img' src={this.state.profileImg[0]} alt="John" style={{width:100}}></img>

                }
                <h1>{this.state.userdetail[0].username}</h1>
                <p className="title">Full name</p>
                <p>email</p>
                <input type = "text" disabled = {this.state.editbio} name = 'bio' value = {bio} onChange = {this.onChangeInfo}></input>
                <img className = 'edit-icon' src="https://img.icons8.com/windows/16/000000/edit.png" alt = "" onClick = {this.onEditBio}/>
                <input type = "text" disabled = {this.state.editaddress} name = 'address' value = {address} onChange = {this.onChangeInfo}></input>
                <img className = 'edit-icon' src="https://img.icons8.com/windows/16/000000/edit.png" alt = "" onClick = {this.onEditAddress} />
                {
                    (this.state.showsubmitbtn === true)?
                    <button className = "edit-submit">Edit Details</button>
                    :<div className = "none-submit"></div>
                }
                
            </div>
        )
    }
}
